import React, { useState, useEffect } from 'react';
import axios from 'axios';

function OrdemServicoList() {
  const [data, setData] = useState([]);
  const [editingData, setEditingData] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    // Fazer uma chamada à sua API para buscar as ordens de serviço.
    axios.get('http://localhost:8000/api/ordem-servico') // Substitua pela URL da sua API
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error('Erro ao buscar dados das ordens de serviço:', error);
      });
  }, []);

  const handleEdit = (ordemServico) => {
    setEditingData(ordemServico);
    setShowModal(true);
  };

  const handleUpdate = () => {
    // Lógica para atualizar a ordem de serviço na API
    setShowModal(false);
  };

  const handleDelete = (ordemServico) => {
    // Lógica para excluir a ordem de serviço na API
  };

  return (
    <div>
      <h1>Lista de Ordens de Serviço</h1>
      <table className="table">
        <thead>
          <tr>
            <th>Número</th>
            <th>Título</th>
            <th>Descrição</th>
            <th>Prioridade</th>
            <th>Responsável</th>
            <th>Data de Início</th>
            <th>Data de Fim</th>
            <th>Status</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {data.map((ordemServico) => (
            <tr key={ordemServico.numero}>
              <td>{ordemServico.numero}</td>
              <td>{ordemServico.titulo}</td>
              <td>{ordemServico.descricao}</td>
              <td>{ordemServico.prioridade}</td>
              <td>{ordemServico.responsavel_id}</td>
              <td>{ordemServico.data_inicio}</td>
              <td>{ordemServico.data_fim}</td>
              <td>{ordemServico.status}</td>
              <td>
                <button className="btn btn-primary" onClick={() => handleEdit(ordemServico)}>Editar</button>
                <button className="btn btn-danger" onClick={() => handleDelete(ordemServico)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal de Edição */}
      {showModal && editingData && (
        <div className="modal">
          <div className="modal-content">
            <h2>Editar Ordem de Serviço</h2>
            {/* Formulário de edição */}
            <button className="btn btn-primary" onClick={handleUpdate}>Salvar</button>
            <button className="btn btn-secondary" onClick={() => setShowModal(false)}>Fechar</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default OrdemServicoList;
