import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [senha, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8000/api/usuarios', {
        email: email,
        senha: senha,
      });
      console.log(response.data); // Você pode tratar a resposta de acordo com suas necessidades
    } catch (error) {
      console.error('Erro ao efetuar login:', error);
    }
  };

  return (
    <div>
      <h2>Acesso</h2>
      <div>
        <label>Email:
          <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
      </div>
      <div>
        <label>Senha:
          <input type="password" value={senha} onChange={(e) => setPassword(e.target.value)} />
        </label>
      </div>
      <button onClick={handleLogin}>Acessar</button>
    </div>
  );
};

export default Login;
