import React, { useState, useEffect } from 'react';
import Modal from 'react-modal'; // Importe a biblioteca react-modal
import { AiOutlineEdit, AiOutlineDelete } from 'react-icons/ai';

Modal.setAppElement('#root'); // Define o elemento raiz do seu aplicativo

const OrdensDeServico = () => {
  const [ordensDeServico, setOrdensDeServico] = useState([]);
  const [selectedOrdem, setSelectedOrdem] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const columns = [
    // Sua definição de colunas
  ];

  const fetchData = async () => {
    // Sua função de busca de dados
  };

  useEffect(() => {
    fetchData();
  }, []);

  const openModal = (ordem) => {
    setSelectedOrdem(ordem);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setSelectedOrdem(null);
    setIsModalOpen(false);
  };

  return (
    <div>
      <h1>Lista de Ordens de Serviço</h1>
      <table>
        <thead>
          <tr>
            {columns.map((column) => (
              <th key={column.Header}>{column.Header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {ordensDeServico.map((ordem) => (
            <tr key={ordem.numero}>
              {columns.map((column) => (
                <td key={column.Header}>
                  {column.accessor === 'titulo' ? (
                    <button onClick={() => openModal(ordem)}>
                      {ordem[column.accessor]}
                    </button>
                  ) : (
                    ordem[column.accessor]
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Detalhes da Ordem de Serviço"
      >
        {selectedOrdem && (
          <div>
            <h2>Detalhes da Ordem de Serviço</h2>
            <p>Número: {selectedOrdem.numero}</p>
            <p>Título: {selectedOrdem.titulo}</p>
            <p>Descrição: {selectedOrdem.descricao}</p>
            <p>Prioridade: {selectedOrdem.prioridade}</p>
            <p>Responsável: {selectedOrdem.responsavel_id}</p>
            <p>Data de Início: {selectedOrdem.data_inicio}</p>
            <p>Data de Término: {selectedOrdem.data_fim}</p>
            <p>Status: {selectedOrdem.status}</p>
            <button onClick={closeModal}>Fechar</button>
            <button>
              <AiOutlineEdit /> Editar
            </button>
            <button>
              <AiOutlineDelete /> Excluir
            </button>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default OrdensDeServico;
